import React, { Component } from 'react';

class Todo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            text: '',
            values: []
        };
    }

    textChangeHandler = event => {
        this.setState({
            text: event.target.value
        });
    }

    addHandler = (event) => {
        alert(event.target.value)
    }


    render() {
        let item = [];
        const { values } = this.state
        debugger;

        this.state.values.map(val => {
            item.push(<li key={val.id}>{val.value}</li>)
        })

        return(
            <div class="row">
                <div class="s12 center">
                    <h4 class="card-panel teal lighten-2 center">Todo Application</h4>
                </div>
                <div class="container">
                    <ul class="collection">
                        {item}
                    </ul>
                </div>
                <div  class="container input-field">
                    <input type="text" onChange={this.textChangeHandler}></input>
                </div>
                <div class="container">
                    <button onClick={this.addHandler}>Submit</button>
                </div>
            </div>
        )
    }
}

export default Todo;
